import styled from 'styled-components';

export const StyledRegister = styled.div`
  width: 100%;
  height: 100vh;
  background: linear-gradient(
      to bottom,
      rgba(0, 0, 0, 0) 0%,
      rgba(0, 0, 0, 1) 100%
    ),
    url('https://cdn.hipwallpaper.com/i/98/21/dUyCkp.jpg');
`;

export const RegisterTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;

  padding: 20px 50px;
`;

export const Logo = styled.img`
  height: 25px;
  margin-right: 2.8rem;
  cursor: pointer;
`;

export const LoginBtn = styled.button`
  background-color: red;
  border: none;
  color: white;
  border-radius: 5px;
  padding: 5px 15px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

export const RegisterContainer = styled.section`
  ${({ theme }) => theme.flexCenterCol}
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;

  color: white;
`;
