import styled from 'styled-components';

export const StyledNav = styled.nav`
  position: fixed;
  width: 100%;
`;

export const NavContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: #141414;

  color: #e5e5e5;
  font-size: 14px;

  padding: 20px 50px;

  div {
    display: flex;
    align-items: center;
  }
`;

export const NavLeft = styled.div`
  span {
    margin: 0 0.6rem;
    cursor: pointer;
  }
`;

export const Logo = styled.img`
  height: 25px;
  margin-right: 2.8rem;
  cursor: pointer;
`;

export const NavRight = styled.div`
  svg,
  span {
    margin: 0 0.8rem;
    cursor: pointer;
  }

  img {
    height: 33px;
    border-radius: 5px;
    cursor: pointer;
  }
`;

export const Profile = styled.div`
  width: 1.5rem;
  margin-left: 0.5rem;
  text-align: center;

  div {
    display: none;
    position: absolute;
    flex-direction: column;
  }

  &:hover + div {
    display: flex;
  }
`;
