import styled from 'styled-components';

export const StyledListItem = styled.div`
  position: relative;
  width: 225px;
  height: 120px;
  background: #fff;
  margin-right: 5px;
  cursor: pointer;

  &:hover {
    width: 325px;
    height: 300px;
    position: absolute;
    top: -150px;
    -webkit-box-shadow: 0px 0px 15px 0px rgba(255, 255, 255, 0.07);
    box-shadow: 0px 0px 15px 0px rgba(255, 255, 255, 0.07);
    border-radius: 5px;
  }
`;

export const ItemImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const PreviewModal = styled.div`
  position: absolute;
  width: 100%;
  height: 140px;
  object-fit: cover;
`;

export const Trailer = styled.video``;
