import styled from 'styled-components';

export const StyledListItem = styled.div`
  width: 225px;
  height: 120px;
  background: #fff;
  margin-right: 5px;
  cursor: pointer;
`;

export const ItemImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const Trailer = styled.video`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 140px;
  object-fit: cover;
`;

export const TrailerInfo = styled.div``;
