import styled from 'styled-components';

export const StyledListItem = styled.div`
  position: absolute;
  width: 225px;
  height: 120px;
  background: #fff;
  margin-right: 5px;
  cursor: pointer;
  z-index: 1;
`;

export const ItemImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const PreviewModal = styled.div`
  position: absolute;
  z-index: 100;
  width: 100%;
  height: 140px;
  object-fit: cover;
  top: 50%;
  left: 0;
`;

export const Trailer = styled.video`
  width: 100%;
  height: 100%;
`;

export const TrailerInfo = styled.div`
  position: absolute;
  width: 300px;
  height: 150px;
  background: lightgreen;
  color: #fff;
`;
