import styled from 'styled-components';

export const StyledListItem = styled.div`
  position: relative;
  width: 225px;
  height: 120px;
  background: #fff;
  margin-right: 5px;
  cursor: pointer;
`;

export const ItemImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const PreviewModal = styled.div`
  position: absolute;
  width: 100%;
  height: 140px;
  object-fit: cover;
  z-index: 999;

  h1 {
    font-size: 20rem;
    font-weight: 700;
    color: #fff;
  }
`;

export const Trailer = styled.video``;
