import styled from 'styled-components';

export const StyledFeatured = styled.div`
  height: 90vh;
  position: relative;
`;

export const FeaturedImg = styled.img`
  width: 100%;
  object-fit: cover;
`;

export const FeaturedInfo = styled.div`
  position: absolute;
  top: 30vh;

  img {
    width: 300px;
  }
`;
