import styled from 'styled-components';

export const StyledListItem = styled.div`
  position: relative;
  width: 225px;
  height: 120px;
  background: #fff;
  margin-right: 5px;
  cursor: pointer;
`;

export const ItemImg = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

export const PreviewModal = styled.div`
  width: 200px;
  height: 400px;
  position: absolute;
`;

export const Trailer = styled.video``;
