import styled from 'styled-components';

export const StyledList = styled.div`
  width: 100%;
  margin-top: 10px;

  h2 {
    font-size: 1.4rem;
    font-weight: 600;
    color: ${({ theme }) => theme.colors.text};
    margin-left: 50px;
  }
`;

export const ListWrapper = styled.div`
  position: relative;
`;

export const ListArrow = styled.div`
  z-index: 2;

  svg {
    position: absolute;
    width: 50px;
    height: 100%;
    background: rgb(22, 22, 22, 0.5);
    color: #fff;
  }
`;

export const ListContainer = styled.div`
  display: flex;
  margin-left: 50px;
  width: max-content;
`;
